if ($_GET[query]) {


    $query = $_GET[query];
    $page = $_GET[page] ? $_GET[page] : "1";

    $start = $page*8 - 8;

    $uquery = urlencode($query);
    $url = "http://ajax.googleapis.com/ajax/services/search/web?v=1.0&q=$uquery&cx=013272987072050942770:8euuocg2hym&rsz=large&start=$start";

    $file = file_get_contents($url);
#    var_dump($file);

    $obj = json_decode($file);


    $hash = $obj->{responseData};
    $results = $hash->{results};
    $cursor = $hash->{cursor};

    $count = $cursor->{estimatedResultCount};
    $query = stripslashes($query);
    if (!$count) {
	print "No results found for <b>$query</b><p>";
	@display_search_block($query);
	include('/data/templates/new/bottom-2008.php');
	exit;
    }
    if ($count > 64) {
	$count = 64;
    }
    ++$start;
    $end = $start + 7;
    if ($end > $count) {
	$end = $count;
    }


    while ($start > $count) {
	$start -=8;
	if ($start < 0) {
