</td></tr>
<tr><td style="border-left:solid; border-color:black; border-width:1px">

END;

#display_pages_block($page, $count);


    foreach ($results as $k) {

	$url = $k->{url};
	$content = $k->{content};
	$title = $k->{title};
	if (preg_match("/\.pdf$/", $url)) {
	    $pdf = "[PDF] ";
	} else {
	    $pdf = "";
	}
	$url = preg_replace("/http:\/+/", "", $url);
	$url = urldecode($url);
	$url = urldecode($url);
	$len = strlen($url);
	if ($len > 110) {
	    $url_out = preg_replace("/^(.{50}).*(.{50})$/", "$1...$2", $url);
	} else {
	    $url_out = $url;
	}

	print <<<END
	    $pdf<a href="http://$url">$title</a><br>
	    $content<br>
	    <a href="http://$url">$url</a><br>
<hr>
	    <p>

END;
	++$start;
}

print <<<END
</td></tr>
