function display_search_block($query) {
    $query = htmlentities($query);
global $this_file;
print <<<END
<table width=100%><tr><td>
<form action="$this_file">
<input type=text name=query value="$query" size=50>
<input type=submit name=submit value="Search">
<br>
<input type=radio checked name=space value=dom> Search UCSF Nephrology<br>
<input type=radio name=space value=ucsf> All of UCSF
&nbsp;
</form>
<div style="float: right" id="branding">Loading...</div>
</td></tr>
</table>
END;

}
function display_pages_block($page, $count) {

global $this_file;
$total_pages = ceil($count / 8);

if ($total_pages <= 1) {
    return(1);
}

#print <<<END
#Go to page:
#END;

print "<center>";

    if ($page == 1) {
	print <<<END
<font color=eeeeee>Previous</font> &nbsp;
END;
	$previous = 1;
    } else if (!$previous) {
	$previous = $page - 1;
	print <<<END
	    <a href="$this_file?query=$_GET[query]&page=$previous">Previous</a> &nbsp;
END;
    }

for ($this_page = 1; $this_page <= $total_pages ; ++$this_page) {




    if ($this_page == $page) {
	print "&nbsp; $page &nbsp;";
    } else {
	print "&nbsp; <a href=\"$this_file?query=$_GET[query]&page=$this_page\">$this_page</a> &nbsp;";
    }
}

if ($page == $total_pages) {
	print <<<END
&nbsp; <font color=eeeeee>Next</font>
END;

} else {
    $next = $page + 1;
print <<<END
&nbsp; <a href="$this_file?query=$_GET[query]&page=$next">Next</a>
END;
}

}
